import React from 'react';
import { Link } from 'react-router-dom';

const VideoCard = ({ video }) => {
  const formatDuration = (seconds) => {
    const minutes = Math.floor(seconds / 60);
    const remainingSeconds = Math.floor(seconds % 60);
    return `${minutes}:${remainingSeconds.toString().padStart(2, '0')}`;
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString();
  };

  // Provide defaults if these arrays are undefined
  const likesCount = Array.isArray(video.likes) ? video.likes.length : 0;
  const viewsCount = Array.isArray(video.views) ? video.views.length : 0;

  return (
    <div className="video-card">
      <div className="video-thumbnail">
        <video
          src={video.url}
          poster={video.thumbnail}
          controls={false}
          muted
          loop
          onMouseOver={(e) => e.target.play()}
          onMouseOut={(e) => e.target.pause()}
        />
        <span className="video-duration">{formatDuration(video.duration)}</span>
        <span className="video-type-badge">{video.videoType}</span>
      </div>

      <div className="video-info">
        <h3>{video.title}</h3>
        <p className="video-description">{video.description}</p>

        <div className="video-meta">
          <span className="video-author">By {video.user?.name || 'Unknown'}</span>
          <span className="video-date">{formatDate(video.createdAt)}</span>
        </div>

        <div className="video-stats">
          <span>{viewsCount} views</span>
          <span>{likesCount} likes</span>
        </div>

        <Link to={`/video/${video._id}`} className="btn btn-primary">
          Watch Video
        </Link>
      </div>
    </div>
  );
};

export default VideoCard;
