import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import { useAuth } from '../context/AuthContext';
import toast from 'react-hot-toast';

const VideoWatch = () => {
  const { id } = useParams();
  const { user } = useAuth();

  const [video, setVideo] = useState(null);
  const [comments, setComments] = useState([]);
  const [liked, setLiked] = useState(false);
  const [likesCount, setLikesCount] = useState(0);
  const [commentText, setCommentText] = useState('');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchVideoData = async () => {
      try {
        // Fetch video details
        const res = await axios.get(`/api/videos/${id}`);
        setVideo(res.data);

        // Fetch comments
        const commentsRes = await axios.get(`/api/videos/${id}/comments`);
        setComments(commentsRes.data);

        // Determine if user liked video
        const userLiked = res.data.likes.some(likeUser => likeUser === user?.id || likeUser === user?._id);
        setLiked(userLiked);

        setLikesCount(res.data.likes.length);

        // Update views (only if logged in)
        if (user) {
          await axios.put(`/api/videos/${id}/views`);
        }

      } catch (error) {
        toast.error('Failed to load video');
      } finally {
        setLoading(false);
      }
    };

    fetchVideoData();
  }, [id, user]);

  const handleLikeToggle = async () => {
    try {
      const res = await axios.put(`/api/videos/${id}/like`);
      setLiked(res.data.liked);
      setLikesCount(res.data.likesCount);
    } catch (error) {
      toast.error('Failed to update like');
    }
  };

  const handleCommentSubmit = async (e) => {
    e.preventDefault();
    if (!commentText.trim()) return;

    try {
      const res = await axios.post(`/api/videos/${id}/comments`, { text: commentText.trim() });
      setComments(prev => [res.data, ...prev]);
      setCommentText('');
      toast.success('Comment added');
    } catch (error) {
      toast.error('Failed to add comment');
    }
  };

  if (loading) return <div>Loading video...</div>;
  if (!video) return <div>Video not found</div>;

  // Hide like and comment buttons if user is creator
  const isCreator = user && user.id === video.user._id;

  return (
    <div className="container video-watch-page">
      <h1>{video.title}</h1>
      <video
        src={video.url}
        poster={video.thumbnail}
        controls
        width="100%"
      />

      <p>{video.description}</p>
      <p>By: {video.user.name}</p>
      <p>Views: {video.views}</p>
      <p>Likes: {likesCount}</p>

      {!isCreator && user && (
        <div className="video-actions">
          <button className="btn btn-primary" onClick={handleLikeToggle}>
            {liked ? 'Unlike' : 'Like'}
          </button>
          {/* Comment button opens the comment form */}
          <button 
            className="btn btn-secondary" 
            onClick={() => document.getElementById('commentInput').focus()}
          >
            Comment
          </button>
        </div>
      )}

      {!isCreator && user && (
        <form onSubmit={handleCommentSubmit} className="comment-form">
          <input
            type="text"
            id="commentInput"
            value={commentText}
            onChange={(e) => setCommentText(e.target.value)}
            placeholder="Write a comment..."
            maxLength={500}
          />
          <button type="submit" className="btn btn-primary" disabled={!commentText.trim()}>
            Post
          </button>
        </form>
      )}

      <div className="comments-section">
        <h3>Comments ({comments.length})</h3>
        {comments.length === 0 && <p>No comments yet.</p>}
        {comments.map(c => (
          <div key={c._id} className="comment">
            <strong>{c.user.name}:</strong> {c.text}
          </div>
        ))}
      </div>
    </div>
  );
};

export default VideoWatch;
