import React, { useState, useEffect } from 'react';
import axios from 'axios';
import VideoCard from './VideoCard';
import toast from 'react-hot-toast';
import { useAuth } from '../context/AuthContext';
import { Card, CardContent } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Skeleton } from '@/components/ui/skeleton';
import { Badge } from '@/components/ui/badge';
import { DropdownMenu, DropdownMenuContent, DropdownMenuItem, DropdownMenuTrigger } from '@/components/ui/dropdown-menu';
import { Video, Upload, Search, Eye, Heart, TrendingUp, Filter, Grid3X3, List, Plus, BarChart3 } from 'lucide-react';
import { Link } from 'react-router-dom';

const MyVideos = () => {
  const { user } = useAuth();
  const [videos, setVideos] = useState([]);
  const [filteredVideos, setFilteredVideos] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [sortBy, setSortBy] = useState('newest');
  const [filterBy, setFilterBy] = useState('all');
  const [viewMode, setViewMode] = useState('grid');

  useEffect(() => {
    if (!user || !user.token) {
      setLoading(false);
      return;
    }

    const fetchMyVideos = async () => {
      setLoading(true);
      try {
        const response = await axios.get('/api/videos/my-videos', {
          headers: {
            Authorization: `Bearer ${user.token}`,
          },
        });
        setVideos(response.data.videos || []);
      } catch (error) {
        console.error('Error fetching my videos:', error);
        toast.error(error.response?.data?.message || 'Failed to load your videos');
      } finally {
        setLoading(false);
      }
    };

    fetchMyVideos();
  }, [user]);

  useEffect(() => {
    let filtered = [...videos];

    // Apply search filter
    if (searchTerm) {
      filtered = filtered.filter(
        (video) =>
          video.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
          video.description.toLowerCase().includes(searchTerm.toLowerCase())
      );
    }

    // Apply type filter
    if (filterBy !== 'all') {
      filtered = filtered.filter((video) => video.videoType === filterBy);
    }

    // Apply sorting
    switch (sortBy) {
      case 'newest':
        filtered.sort((a, b) => new Date(b.createdAt).getTime() - new Date(a.createdAt).getTime());
        break;
      case 'oldest':
        filtered.sort((a, b) => new Date(a.createdAt).getTime() - new Date(b.createdAt).getTime());
        break;
      case 'popular':
        filtered.sort((a, b) => (b.views?.length || 0) - (a.views?.length || 0));
        break;
      case 'liked':
        filtered.sort((a, b) => (b.likes?.length || 0) - (a.likes?.length || 0));
        break;
    }

    setFilteredVideos(filtered);
  }, [videos, searchTerm, sortBy, filterBy]);

  // Calculate stats
  const totalViews = videos.reduce((sum, video) => sum + (video.views?.length || 0), 0);
  const totalLikes = videos.reduce((sum, video) => sum + (video.likes?.length || 0), 0);
  const shortVideos = videos.filter((v) => v.videoType === 'short').length;
  const longVideos = videos.filter((v) => v.videoType === 'long').length;

  const stats = [
    { label: 'Total Videos', value: videos.length, icon: Video, color: 'text-blue-600' },
    { label: 'Total Views', value: totalViews.toLocaleString(), icon: Eye, color: 'text-green-600' },
    { label: 'Total Likes', value: totalLikes.toLocaleString(), icon: Heart, color: 'text-red-600' },
    { label: 'This Month', value: '12', icon: TrendingUp, color: 'text-purple-600' },
  ];

  const sortOptions = [
    { key: 'newest', label: 'Newest First' },
    { key: 'oldest', label: 'Oldest First' },
    { key: 'popular', label: 'Most Viewed' },
    { key: 'liked', label: 'Most Liked' },
  ];

  const filterOptions = [
    { key: 'all', label: 'All Videos', count: videos.length },
    { key: 'short', label: 'Short Videos', count: shortVideos },
    { key: 'long', label: 'Long Videos', count: longVideos },
  ];

  const LoadingSkeleton = () => (
    <div className="space-y-6">
      {/* Stats Skeleton */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
        {Array.from({ length: 4 }).map((_, index) => (
          <Card key={index} className="border-0 shadow-lg">
            <CardContent className="p-6">
              <div className="flex items-center justify-between">
                <div>
                  <Skeleton className="h-4 w-20 mb-2" />
                  <Skeleton className="h-8 w-16" />
                </div>
                <Skeleton className="h-12 w-12 rounded-full" />
              </div>
            </CardContent>
          </Card>
        ))}
      </div>

      {/* Videos Skeleton */}
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
        {Array.from({ length: 8 }).map((_, index) => (
          <Card key={index} className="overflow-hidden border-0 shadow-lg">
            <Skeleton className="aspect-video w-full" />
            <CardContent className="p-4">
              <Skeleton className="h-4 w-3/4 mb-2" />
              <Skeleton className="h-3 w-full mb-2" />
              <Skeleton className="h-3 w-2/3" />
            </CardContent>
          </Card>
        ))}
      </div>
    </div>
  );

  if (loading) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-slate-50 to-slate-100 p-4">
        <div className="max-w-7xl mx-auto">
          <div className="mb-8">
            <Skeleton className="h-10 w-48 mb-2" />
            <Skeleton className="h-6 w-96" />
          </div>
          <LoadingSkeleton />
        </div>
      </div>
    );
  }

  if (!user) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-slate-50 to-slate-100 flex items-center justify-center p-4">
        <Card className="max-w-md mx-auto border-0 shadow-xl bg-white/80 backdrop-blur-sm">
          <CardContent className="p-8 text-center">
            <div className="w-16 h-16 mx-auto mb-4 bg-red-100 rounded-full flex items-center justify-center">
              <Video className="w-8 h-8 text-red-600" />
            </div>
            <h3 className="text-xl font-semibold text-gray-900 mb-2">Authentication Required</h3>
            <p className="text-gray-600 mb-4">Please log in to view your videos.</p>
            <Button asChild className="bg-gradient-to-r from-blue-600 to-purple-600 text-white">
              <Link to="/login">Go to Login</Link>
            </Button>
          </CardContent>
        </Card>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 to-slate-100 p-4">
      <div className="max-w-7xl mx-auto">
        {/* Header */}
        <div className="mb-8">
          <div className="flex flex-col md:flex-row md:items-center md:justify-between gap-4">
            <div>
              <h1 className="text-4xl font-bold text-gray-900 mb-2">My Videos</h1>
              <p className="text-gray-600 text-lg">Manage and track your video content</p>
            </div>
            <Button
              asChild
              className="bg-gradient-to-r from-blue-600 to-purple-600 hover:from-blue-700 hover:to-purple-700 text-white shadow-lg hover:shadow-xl transition-all duration-200"
            >
              <Link to="/upload" className="flex items-center gap-2">
                <Plus className="w-4 h-4" />
                Upload New Video
              </Link>
            </Button>
          </div>
        </div>

        {/* Stats Cards */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4 mb-8">
          {stats.map((stat, index) => {
            const IconComponent = stat.icon;
            return (
              <Card key={index} className="border-0 shadow-lg bg-white/80 backdrop-blur-sm">
                <CardContent className="p-6">
                  <div className="flex items-center justify-between">
                    <div>
                      <p className="text-sm font-medium text-gray-600">{stat.label}</p>
                      <p className="text-2xl font-bold text-gray-900">{stat.value}</p>
                    </div>
                    <div className={`w-12 h-12 rounded-full bg-gray-100 flex items-center justify-center ${stat.color}`}>
                      <IconComponent className="w-6 h-6" />
                    </div>
                  </div>
                </CardContent>
              </Card>
            );
          })}
        </div>

        {videos.length === 0 ? (
          /* Empty State */
          <Card className="border-0 shadow-xl bg-white/80 backdrop-blur-sm">
            <CardContent className="p-12 text-center">
              <div className="w-20 h-20 mx-auto mb-6 bg-blue-100 rounded-full flex items-center justify-center">
                <Video className="w-10 h-10 text-blue-600" />
              </div>
              <h3 className="text-2xl font-bold text-gray-900 mb-2">No videos yet</h3>
              <p className="text-gray-600 mb-6 max-w-md mx-auto">
                Start your content creation journey by uploading your first video. Share your story with the world!
              </p>
              <div className="flex flex-col sm:flex-row gap-3 justify-center">
                <Button
                  asChild
                  className="bg-gradient-to-r from-blue-600 to-purple-600 hover:from-blue-700 hover:to-purple-700 text-white"
                >
                  <Link to="/upload" className="flex items-center gap-2">
                    <Upload className="w-4 h-4" />
                    Upload Your First Video
                  </Link>
                </Button>
                <Button asChild variant="outline" className="bg-white/50">
                  <Link to="/videos" className="flex items-center gap-2">
                    <Eye className="w-4 h-4" />
                    Explore Other Videos
                  </Link>
                </Button>
              </div>
            </CardContent>
          </Card>
        ) : (
          <>
            {/* Controls */}
            <div className="flex flex-col lg:flex-row lg:items-center lg:justify-between gap-4 mb-6">
              {/* Search */}
              <div className="relative max-w-md">
                <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-4 h-4" />
                <Input
                  type="text"
                  placeholder="Search your videos..."
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                  className="pl-10 bg-white/80 backdrop-blur-sm border-gray-200 focus:border-blue-500 focus:ring-blue-500"
                />
              </div>

              <div className="flex items-center gap-2">
                {/* Filter */}
                <DropdownMenu>
                  <DropdownMenuTrigger asChild>
                    <Button variant="outline" size="sm" className="bg-white/80 backdrop-blur-sm">
                      <Filter className="w-4 h-4 mr-2" />
                      {filterOptions.find((f) => f.key === filterBy)?.label}
                    </Button>
                  </DropdownMenuTrigger>
                  <DropdownMenuContent align="end" className="bg-white/95 backdrop-blur-sm">
                    {filterOptions.map((option) => (
                      <DropdownMenuItem
                        key={option.key}
                        onClick={() => setFilterBy(option.key)}
                        className={filterBy === option.key ? 'bg-blue-50 text-blue-600' : ''}
                      >
                        <span className="flex items-center justify-between w-full">
                          {option.label}
                          <Badge variant="secondary" className="ml-2">
                            {option.count}
                          </Badge>
                        </span>
                      </DropdownMenuItem>
                    ))}
                  </DropdownMenuContent>
                </DropdownMenu>

                {/* Sort */}
                <DropdownMenu>
                  <DropdownMenuTrigger asChild>
                    <Button variant="outline" size="sm" className="bg-white/80 backdrop-blur-sm">
                      <BarChart3 className="w-4 h-4 mr-2" />
                      {sortOptions.find((s) => s.key === sortBy)?.label}
                    </Button>
                  </DropdownMenuTrigger>
                  <DropdownMenuContent align="end" className="bg-white/95 backdrop-blur-sm">
                    {sortOptions.map((option) => (
                      <DropdownMenuItem
                        key={option.key}
                        onClick={() => setSortBy(option.key)}
                        className={sortBy === option.key ? 'bg-blue-50 text-blue-600' : ''}
                      >
                        {option.label}
                      </DropdownMenuItem>
                    ))}
                  </DropdownMenuContent>
                </DropdownMenu>

                {/* View Mode */}
                <div className="flex items-center bg-white/80 backdrop-blur-sm rounded-lg p-1 border">
                  <Button
                    variant={viewMode === 'grid' ? 'default' : 'ghost'}
                    size="sm"
                    onClick={() => setViewMode('grid')}
                    className="p-2"
                  >
                    <Grid3X3 className="w-4 h-4" />
                  </Button>
                  <Button
                    variant={viewMode === 'list' ? 'default' : 'ghost'}
                    size="sm"
                    onClick={() => setViewMode('list')}
                    className="p-2"
                  >
                    <List className="w-4 h-4" />
                  </Button>
                </div>
              </div>
            </div>

            {/* Results */}
            <div className="mb-4">
              <p className="text-gray-600">
                {searchTerm ? (
                  <>
                    Found <span className="font-semibold text-gray-900">{filteredVideos.length}</span> videos
                    {searchTerm && (
                      <>
                        {' '}
                        for "<span className="font-semibold text-blue-600">{searchTerm}</span>"
                      </>
                    )}
                  </>
                ) : (
                  <>
                    Showing <span className="font-semibold text-gray-900">{filteredVideos.length}</span> of{' '}
                    <span className="font-semibold text-gray-900">{videos.length}</span> videos
                  </>
                )}
              </p>
            </div>

            {/* Videos Grid */}
            {filteredVideos.length === 0 ? (
              <Card className="border-0 shadow-lg bg-white/80 backdrop-blur-sm">
                <CardContent className="p-8 text-center">
                  <div className="w-16 h-16 mx-auto mb-4 bg-gray-100 rounded-full flex items-center justify-center">
                    <Search className="w-8 h-8 text-gray-400" />
                  </div>
                  <h3 className="text-xl font-semibold text-gray-900 mb-2">No videos found</h3>
                  <p className="text-gray-600">Try adjusting your search terms or filters.</p>
                  <Button onClick={() => setSearchTerm('')} variant="outline" className="mt-4">
                    Clear Search
                  </Button>
                </CardContent>
              </Card>
            ) : (
              <div
                className={`grid gap-6 ${
                  viewMode === 'grid'
                    ? 'grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4'
                    : 'grid-cols-1 max-w-4xl mx-auto'
                }`}
              >
                {filteredVideos.map((video) => (
                  <VideoCard key={video._id} video={video} viewMode={viewMode} />
                ))}
              </div>
            )}
          </>
        )}
      </div>
    </div>
  );
};

export default MyVideos;
