import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import toast from 'react-hot-toast';

const VideoWatch = () => {
  const { id } = useParams();
  const [video, setVideo] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchVideo = async () => {
      try {
        const response = await axios.get(`/api/videos/${id}`);
        setVideo(response.data);
        
        // Optionally, increment view count on video watch
        await axios.put(`/api/videos/${id}/views`);
      } catch (error) {
        toast.error('Failed to load video');
      } finally {
        setLoading(false);
      }
    };

    fetchVideo();
  }, [id]);

  if (loading) return <div>Loading video...</div>;
  if (!video) return <div>Video not found.</div>;

  return (
    <div className="container video-watch">
      <h2>{video.title}</h2>
      <video 
        src={video.url} 
        controls 
        autoPlay 
        style={{ width: '100%', maxHeight: '500px' }}
      />
      <p>{video.description}</p>
      <p>By: {video.user.name}</p>
      <p>Views: {video.views}</p>
      <p>Likes: {video.likes.length}</p>
      <p>Uploaded on: {new Date(video.createdAt).toLocaleDateString()}</p>
    </div>
  );
};

export default VideoWatch;
