import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { Card, CardContent } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Avatar, AvatarFallback, AvatarImage } from '@/components/ui/avatar';
import { Badge } from '@/components/ui/badge';
import { Eye, Heart, Clock, Play } from 'lucide-react';

const VideoCard = ({ video }) => {
  const [isHovered, setIsHovered] = useState(false);
  const [imageError, setImageError] = useState(false);

  const formatDuration = (seconds) => {
    const minutes = Math.floor(seconds / 60);
    const remainingSeconds = Math.floor(seconds % 60);
    return `${minutes}:${remainingSeconds.toString().padStart(2, '0')}`;
  };

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    const now = new Date();
    const diffTime = Math.abs(now.getTime() - date.getTime());
    const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));

    if (diffDays === 1) return '1 day ago';
    if (diffDays < 7) return `${diffDays} days ago`;
    if (diffDays < 30) return `${Math.floor(diffDays / 7)} weeks ago`;
    if (diffDays < 365) return `${Math.floor(diffDays / 30)} months ago`;
    return `${Math.floor(diffDays / 365)} years ago`;
  };

  const formatCount = (count) => {
    if (count < 1000) return count.toString();
    if (count < 1000000) return `${(count / 1000).toFixed(1)}K`;
    return `${(count / 1000000).toFixed(1)}M`;
  };

  const likesCount = Array.isArray(video.likes) ? video.likes.length : 0;
  const viewsCount = Array.isArray(video.views) ? video.views.length : 0;

  const getTypeColor = (type) => {
    switch (type?.toLowerCase()) {
      case 'short':
        return 'bg-green-500 hover:bg-green-600';
      case 'long':
        return 'bg-purple-500 hover:bg-purple-600';
      default:
        return 'bg-blue-500 hover:bg-blue-600';
    }
  };

  return (
    <Card
      className="group overflow-hidden border-0 shadow-lg hover:shadow-2xl transition-all duration-300 transform hover:-translate-y-1 bg-white/80 backdrop-blur-sm"
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
    >
      {/* Video Thumbnail */}
      <div className="relative aspect-video bg-gray-900 overflow-hidden">
        {!imageError && video.thumbnail ? (
          <img
            src={video.thumbnail || '/placeholder.svg'}
            alt={video.title}
            className="w-full h-full object-cover transition-transform duration-300 group-hover:scale-105"
            onError={() => setImageError(true)}
          />
        ) : (
          <div className="w-full h-full bg-gradient-to-br from-gray-800 to-gray-900 flex items-center justify-center">
            <Play className="w-12 h-12 text-white/60" />
          </div>
        )}

        {/* Overlay */}
        <div
          className={`absolute inset-0 bg-black/20 transition-opacity duration-300 ${
            isHovered ? 'opacity-100' : 'opacity-0'
          }`}
        />

        {/* Duration Badge */}
        <div className="absolute bottom-2 right-2">
          <Badge className="bg-black/80 text-white text-xs px-2 py-1 flex items-center gap-1">
            <Clock className="w-3 h-3" />
            {formatDuration(video.duration)}
          </Badge>
        </div>

        {/* Video Type Badge */}
        <div className="absolute top-2 left-2">
          <Badge className={`text-white text-xs px-2 py-1 ${getTypeColor(video.videoType)}`}>
            {video.videoType || 'Video'}
          </Badge>
        </div>

        {/* Play Button Overlay */}
        <div
          className={`absolute inset-0 flex items-center justify-center transition-opacity duration-300 ${
            isHovered ? 'opacity-100' : 'opacity-0'
          }`}
        >
          <div className="w-16 h-16 bg-white/90 rounded-full flex items-center justify-center shadow-lg transform transition-transform duration-200 hover:scale-110">
            <Play className="w-6 h-6 text-gray-900 ml-1" />
          </div>
        </div>
      </div>

      <CardContent className="p-4">
        {/* Video Title */}
        <h3 className="font-semibold text-gray-900 mb-2 line-clamp-2 group-hover:text-blue-600 transition-colors duration-200">
          {video.title}
        </h3>

        {/* Video Description */}
        <p className="text-sm text-gray-600 mb-3 line-clamp-2 leading-relaxed">{video.description}</p>

        {/* Creator Info */}
        <div className="flex items-center gap-2 mb-3">
          <Avatar className="h-6 w-6">
            <AvatarImage src={video.user?.avatar || '/placeholder.svg'} alt={video.user?.name} />
            <AvatarFallback className="bg-gradient-to-br from-blue-500 to-purple-600 text-white text-xs">
              {video.user?.name?.charAt(0)?.toUpperCase() || 'U'}
            </AvatarFallback>
          </Avatar>
          <div className="flex-1 min-w-0">
            <p className="text-sm font-medium text-gray-700 truncate">{video.user?.name || 'Unknown'}</p>
            <p className="text-xs text-gray-500">{formatDate(video.createdAt)}</p>
          </div>
        </div>

        {/* Video Stats */}
        <div className="flex items-center justify-between mb-4 text-xs text-gray-500">
          <div className="flex items-center gap-3">
            <span className="flex items-center gap-1">
              <Eye className="w-3 h-3" />
              {formatCount(viewsCount)}
            </span>
            <span className="flex items-center gap-1">
              <Heart className="w-3 h-3" />
              {formatCount(likesCount)}
            </span>
          </div>
        </div>

        {/* Watch Button */}
        <Link to={`/video/${video._id}`} className="block">
          <Button className="w-full bg-gradient-to-r from-blue-600 to-purple-600 hover:from-blue-700 hover:to-purple-700 text-white transition-all duration-200 shadow-md hover:shadow-lg">
            <Play className="w-4 h-4 mr-2" />
            Watch Video
          </Button>
        </Link>
      </CardContent>
    </Card>
  );
};

export default VideoCard;
