import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { Toaster } from 'react-hot-toast';
import { AuthProvider } from './context/AuthContext';
import Navbar from './components/Navbar';
import Dashboard from './components/Dashboard';
import VideoList from './components/VideoList';
import VideoWatch from './components/VideoWatch';
import UploadVideo from './components/UploadVideo';
import MyVideos from './components/MyVideos';
import Register from './components/Register';
import Login from './components/Login'; // You'll need to create this similar to Register
import ProtectedRoute from './components/ProtectedRoute'; // Optional: for protected routes
import './App.css';

function App() {
  return (
    <AuthProvider>
      <Router>
        <div className="App min-h-screen bg-gradient-to-br from-slate-50 to-slate-100">
          <Navbar />
          <main>
            <Routes>
              {/* Public Routes */}
              <Route path="/" element={<Dashboard />} />
              <Route path="/videos" element={<VideoList />} />
              <Route path="/video/:id" element={<VideoWatch />} />
              <Route path="/register" element={<Register />} />
              <Route path="/login" element={<Login />} />
              
              {/* Protected Routes (require authentication) */}
              <Route 
                path="/upload" 
                element={
                  <ProtectedRoute>
                    <UploadVideo />
                  </ProtectedRoute>
                } 
              />
              <Route 
                path="/my-videos" 
                element={
                  <ProtectedRoute>
                    <MyVideos />
                  </ProtectedRoute>
                } 
              />
              
              {/* Catch all route - 404 page */}
              <Route path="*" element={<NotFound />} />
            </Routes>
          </main>
          
          {/* Toast notifications */}
          <Toaster
            position="top-right"
            toastOptions={{
              duration: 4000,
              style: {
                background: '#363636',
                color: '#fff',
              },
              success: {
                duration: 3000,
                theme: {
                  primary: 'green',
                  secondary: 'black',
                },
              },
            }}
          />
        </div>
      </Router>
    </AuthProvider>
  );
}

// Optional: 404 Not Found component
const NotFound = () => (
  <div className="min-h-screen bg-gradient-to-br from-slate-50 to-slate-100 flex items-center justify-center p-4">
    <div className="text-center">
      <h1 className="text-6xl font-bold text-gray-900 mb-4">404</h1>
      <p className="text-xl text-gray-600 mb-8">Page not found</p>
      <a 
        href="/" 
        className="bg-gradient-to-r from-blue-600 to-purple-600 text-white px-6 py-3 rounded-lg hover:from-blue-700 hover:to-purple-700 transition-all duration-200"
      >
        Go Home
      </a>
    </div>
  </div>
);

export default App;
