import React, { useState, useEffect } from 'react';
import axios from 'axios';
import VideoCard from './VideoCard';
import toast from 'react-hot-toast';
import { useAuth } from '../context/AuthContext';

const MyVideos = () => {
  const { user } = useAuth();
  const [videos, setVideos] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    if (!user) return;

    const fetchMyVideos = async () => {
      setLoading(true);
      try {
        const response = await axios.get('/api/videos/my-videos', {
          headers: {
            Authorization: `Bearer ${user.token}`, // assuming your user object stores token here
          },
        });
        setVideos(response.data.videos || []);
      } catch (error) {
        toast.error(error.response?.data?.message || 'Failed to load your videos');
      } finally {
        setLoading(false);
      }
    };

    fetchMyVideos();
  }, [user]);

  if (loading) return <p>Loading your videos...</p>;

  if (videos.length === 0) return <p>You have no uploaded videos yet.</p>;

  return (
    <div className="container">
      <h1>My Videos</h1>
      <div className="grid">
        {videos.map((video) => (
          <VideoCard key={video._id} video={video} />
        ))}
      </div>
    </div>
  );
};

export default MyVideos;
