import React, { useEffect, useState } from 'react';
import axios from 'axios';
import toast from 'react-hot-toast';
import { useAuth } from '../context/AuthContext';

const VideoWatch = ({ videoId }) => {
  const { user } = useAuth();
  const [video, setVideo] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchVideo = async () => {
      setLoading(true);
      try {
        const res = await axios.get(`/api/videos/${videoId}`);
        setVideo(res.data.video);
      } catch (error) {
        toast.error(error.response?.data?.message || 'Failed to fetch video');
      } finally {
        setLoading(false);
      }
    };

    fetchVideo();
  }, [videoId]);

  if (loading) return <div>Loading video...</div>;
  if (!video) return <div>Video not found.</div>;

  const isCreator = user?._id === video.user?._id;

  return (
    <div className="video-watch-container">
      <h1>{video.title}</h1>
      <video controls src={video.url} poster={video.thumbnail} />
      <p>{video.description}</p>
      <p>By: {video.user?.name || 'Unknown'}</p>
      <p>Views: {Array.isArray(video.views) ? video.views.length : 0}</p>
      <p>Likes: {Array.isArray(video.likes) ? video.likes.length : 0}</p>

      {!isCreator && (
        <div className="video-actions">
          <button /* Like button logic */>Like</button>
          <button /* Comment button logic */>Comment</button>
        </div>
      )}
    </div>
  );
};

export default VideoWatch;
