import React, { useState, useEffect } from 'react';
import axios from 'axios';
import VideoCard from './VideoCard';
import toast from 'react-hot-toast';

const MyVideos = () => {
  const [videos, setVideos] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchMyVideos();
  }, []);

  const fetchMyVideos = async () => {
    try {
      const response = await axios.get('/api/videos/user/my-videos');
      setVideos(response.data);
    } catch (error) {
      console.error('Error fetching my videos:', error);
      toast.error('Failed to load your videos');
    } finally {
      setLoading(false);
    }
  };

  const handleDelete = async (videoId) => {
    if (window.confirm('Are you sure you want to delete this video?')) {
      try {
        await axios.delete(`/api/videos/${videoId}`);
        toast.success('Video deleted successfully');
        fetchMyVideos(); // Refresh the list
      } catch (error) {
        console.error('Error deleting video:', error);
        toast.error('Failed to delete video');
      }
    }
  };

  if (loading) {
    return <div className="loading">Loading your videos...</div>;
  }

  return (
    <div className="container">
      <h1>My Videos</h1>
      
      {videos.length === 0 ? (
        <div className="empty-state">
          <p>You haven't uploaded any videos yet.</p>
          <a href="/upload" className="btn btn-primary">Upload Your First Video</a>
        </div>
      ) : (
        <div className="grid">
          {videos.map(video => (
            <div key={video._id} className="video-card-container">
              <VideoCard video={video} />
              <div className="video-actions">
                <button 
                  onClick={() => handleDelete(video._id)} 
                  className="btn btn-danger"
                >
                  Delete
                </button>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default MyVideos;
