import React, { useState, useEffect } from 'react';
import axios from 'axios';
import VideoCard from './VideoCard';
import { Card, CardContent } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Skeleton } from '@/components/ui/skeleton';
import { Search, Clock, Zap } from 'lucide-react';

const VideoList = () => {
  const [videos, setVideos] = useState([]);
  const [loading, setLoading] = useState(true);
  const [filter, setFilter] = useState('all');
  const [searchTerm, setSearchTerm] = useState('');
  const [filteredVideos, setFilteredVideos] = useState([]);

  useEffect(() => {
    const fetchVideos = async () => {
      setLoading(true);
      try {
        const params = filter !== 'all' ? { type: filter } : {};
        const response = await axios.get('/api/videos', { params });
        setVideos(response.data.videos);
      } catch (error) {
        console.error('Error fetching videos:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchVideos();
  }, [filter]);

  useEffect(() => {
    const filtered = videos.filter(
      (video) =>
        video.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
        video.description.toLowerCase().includes(searchTerm.toLowerCase()) ||
        video.user.name.toLowerCase().includes(searchTerm.toLowerCase())
    );
    setFilteredVideos(filtered);
  }, [videos, searchTerm]);

  const filterOptions = [
    { value: 'all', label: 'All Videos', icon: Search, color: 'bg-blue-500' },
    { value: 'short', label: 'Short Videos', icon: Zap, color: 'bg-green-500' },
    { value: 'long', label: 'Long Videos', icon: Clock, color: 'bg-purple-500' },
  ];

  const LoadingSkeleton = () => (
    <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
      {Array.from({ length: 8 }).map((_, index) => (
        <Card key={index} className="overflow-hidden border-0 shadow-lg">
          <Skeleton className="aspect-video w-full" />
          <CardContent className="p-4">
            <Skeleton className="h-4 w-3/4 mb-2" />
            <Skeleton className="h-3 w-full mb-2" />
            <Skeleton className="h-3 w-2/3 mb-3" />
            <div className="flex justify-between items-center">
              <Skeleton className="h-3 w-16" />
              <Skeleton className="h-3 w-16" />
            </div>
          </CardContent>
        </Card>
      ))}
    </div>
  );

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 to-slate-100 p-4">
      <div className="max-w-7xl mx-auto">
        {/* Header Section */}
        <div className="mb-8">
          <div className="text-center mb-6">
            <h1 className="text-4xl font-bold text-gray-900 mb-2">Explore Videos</h1>
            <p className="text-gray-600 text-lg">Discover amazing content from creators around the world</p>
          </div>

          {/* Search Bar */}
          <div className="max-w-2xl mx-auto mb-6">
            <div className="relative">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-5 h-5" />
              <Input
                type="text"
                placeholder="Search videos, creators, or topics..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="pl-10 pr-4 py-3 text-lg border-0 shadow-lg bg-white/80 backdrop-blur-sm focus:ring-2 focus:ring-blue-500"
              />
            </div>
          </div>

          {/* Filter Buttons */}
          <div className="flex flex-wrap justify-center gap-3">
            {filterOptions.map((option) => {
              const IconComponent = option.icon;
              const isActive = filter === option.value;

              return (
                <Button
                  key={option.value}
                  onClick={() => setFilter(option.value)}
                  variant={isActive ? 'default' : 'outline'}
                  className={`flex items-center gap-2 px-6 py-3 rounded-full transition-all duration-200 ${
                    isActive
                      ? `${option.color} hover:opacity-90 text-white shadow-lg`
                      : 'bg-white/80 backdrop-blur-sm hover:bg-white border-gray-200 text-gray-700 hover:text-gray-900 shadow-md'
                  }`}
                >
                  <IconComponent className="w-4 h-4" />
                  {option.label}
                </Button>
              );
            })}
          </div>
        </div>

        {/* Results Count */}
        {!loading && (
          <div className="mb-6 text-center">
            <p className="text-gray-600">
              {searchTerm ? (
                <>
                  Found <span className="font-semibold text-gray-900">{filteredVideos.length}</span> videos
                  {searchTerm && (
                    <>
                      {' '}
                      for "<span className="font-semibold text-blue-600">{searchTerm}</span>"
                    </>
                  )}
                </>
              ) : (
                <>
                  Showing <span className="font-semibold text-gray-900">{filteredVideos.length}</span> videos
                </>
              )}
            </p>
          </div>
        )}

        {/* Content */}
        {loading ? (
          <LoadingSkeleton />
        ) : filteredVideos.length === 0 ? (
          <div className="text-center py-16">
            <Card className="max-w-md mx-auto border-0 shadow-xl bg-white/80 backdrop-blur-sm">
              <CardContent className="p-8">
                <div className="w-16 h-16 mx-auto mb-4 bg-gray-100 rounded-full flex items-center justify-center">
                  <Search className="w-8 h-8 text-gray-400" />
                </div>
                <h3 className="text-xl font-semibold text-gray-900 mb-2">
                  {searchTerm ? 'No videos found' : 'No videos available'}
                </h3>
                <p className="text-gray-600">
                  {searchTerm
                    ? `Try adjusting your search terms or browse all videos.`
                    : 'Check back later for new content!'}
                </p>
                {searchTerm && (
                  <Button onClick={() => setSearchTerm('')} variant="outline" className="mt-4">
                    Clear Search
                  </Button>
                )}
              </CardContent>
            </Card>
          </div>
        ) : (
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
            {filteredVideos.map((video) => (
              <VideoCard key={video._id} video={video} />
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default VideoList;
