import React, { useEffect, useState } from 'react';
import axios from 'axios';
import toast from 'react-hot-toast';
import { useAuth } from '../context/AuthContext';
import { Card, CardContent } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Avatar, AvatarFallback, AvatarImage } from '@/components/ui/avatar';
import { Skeleton } from '@/components/ui/skeleton';
import { Heart, MessageCircle, Eye, Calendar, User } from 'lucide-react';

const VideoWatch = ({ videoId }) => {
  const { user } = useAuth();
  const [video, setVideo] = useState(null);
  const [loading, setLoading] = useState(true);
  const [isLiked, setIsLiked] = useState(false);

  useEffect(() => {
    const fetchVideo = async () => {
      setLoading(true);
      try {
        const res = await axios.get(`/api/videos/${videoId}`);
        setVideo(res.data.video);
        // Check if user has liked the video
        if (res.data.video.likes && user) {
          setIsLiked(res.data.video.likes.includes(user._id));
        }
      } catch (error) {
        toast.error(error.response?.data?.message || 'Failed to fetch video');
      } finally {
        setLoading(false);
      }
    };

    fetchVideo();
  }, [videoId, user]);

  const handleLike = async () => {
    if (!user || !video) return;

    try {
      const res = await axios.post(`/api/videos/${videoId}/like`);
      setVideo(res.data.video);
      setIsLiked(!isLiked);
      toast.success(isLiked ? 'Removed from likes' : 'Added to likes');
    } catch (error) {
      toast.error(error.response?.data?.message || 'Failed to like video');
    }
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'long',
      day: 'numeric',
    });
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-slate-50 to-slate-100 p-4">
        <div className="max-w-6xl mx-auto">
          <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
            {/* Video Player Skeleton */}
            <div className="lg:col-span-2">
              <Card className="overflow-hidden border-0 shadow-xl">
                <Skeleton className="aspect-video w-full" />
                <CardContent className="p-6">
                  <Skeleton className="h-8 w-3/4 mb-4" />
                  <div className="flex items-center gap-4 mb-4">
                    <Skeleton className="h-12 w-12 rounded-full" />
                    <div className="space-y-2">
                      <Skeleton className="h-4 w-32" />
                      <Skeleton className="h-3 w-24" />
                    </div>
                  </div>
                  <Skeleton className="h-20 w-full" />
                </CardContent>
              </Card>
            </div>

            {/* Sidebar Skeleton */}
            <div className="space-y-4">
              <Card className="border-0 shadow-lg">
                <CardContent className="p-6">
                  <Skeleton className="h-6 w-24 mb-4" />
                  <div className="space-y-3">
                    <Skeleton className="h-4 w-full" />
                    <Skeleton className="h-4 w-full" />
                    <Skeleton className="h-4 w-3/4" />
                  </div>
                </CardContent>
              </Card>
            </div>
          </div>
        </div>
      </div>
    );
  }

  if (!video) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-slate-50 to-slate-100 flex items-center justify-center">
        <Card className="border-0 shadow-xl">
          <CardContent className="p-12 text-center">
            <div className="w-16 h-16 mx-auto mb-4 bg-red-100 rounded-full flex items-center justify-center">
              <Eye className="w-8 h-8 text-red-600" />
            </div>
            <h2 className="text-2xl font-bold text-gray-900 mb-2">Video Not Found</h2>
            <p className="text-gray-600">The video you're looking for doesn't exist or has been removed.</p>
          </CardContent>
        </Card>
      </div>
    );
  }

  const isCreator = user?._id === video.user?._id;
  const viewCount = Array.isArray(video.views) ? video.views.length : 0;
  const likeCount = Array.isArray(video.likes) ? video.likes.length : 0;

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 to-slate-100 p-4">
      <div className="max-w-6xl mx-auto">
        <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
          {/* Main Video Section */}
          <div className="lg:col-span-2">
            <Card className="overflow-hidden border-0 shadow-xl bg-white/80 backdrop-blur-sm">
              {/* Video Player */}
              <div className="relative aspect-video bg-black">
                <video
                  controls
                  src={video.url}
                  poster={video.thumbnail}
                  className="w-full h-full object-contain"
                  preload="metadata"
                >
                  Your browser does not support the video tag.
                </video>
              </div>

              <CardContent className="p-6">
                {/* Video Title */}
                <h1 className="text-2xl lg:text-3xl font-bold text-gray-900 mb-4 leading-tight">{video.title}</h1>

                {/* Creator Info & Actions */}
                <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between gap-4 mb-6">
                  <div className="flex items-center gap-3">
                    <Avatar className="h-12 w-12 ring-2 ring-white shadow-lg">
                      <AvatarImage src={video.user.avatar || '/placeholder.svg'} alt={video.user.name} />
                      <AvatarFallback className="bg-gradient-to-br from-blue-500 to-purple-600 text-white font-semibold">
                        {video.user.name?.charAt(0)?.toUpperCase() || 'U'}
                      </AvatarFallback>
                    </Avatar>
                    <div>
                      <p className="font-semibold text-gray-900">{video.user.name || 'Unknown Creator'}</p>
                      <p className="text-sm text-gray-500 flex items-center gap-1">
                        <Calendar className="w-3 h-3" />
                        {formatDate(video.createdAt)}
                      </p>
                    </div>
                  </div>

                  {!isCreator && (
                    <div className="flex items-center gap-2">
                      <Button
                        onClick={handleLike}
                        variant={isLiked ? 'default' : 'outline'}
                        size="sm"
                        className={`flex items-center gap-2 ${
                          isLiked
                            ? 'bg-red-500 hover:bg-red-600 text-white'
                            : 'hover:bg-red-50 hover:text-red-600 hover:border-red-200'
                        }`}
                      >
                        <Heart className={`w-4 h-4 ${isLiked ? 'fill-current' : ''}`} />
                        Like
                      </Button>
                      <Button
                        variant="outline"
                        size="sm"
                        className="flex items-center gap-2 hover:bg-blue-50 hover:text-blue-600 hover:border-blue-200 bg-transparent"
                      >
                        <MessageCircle className="w-4 h-4" />
                        Comment
                      </Button>
                    </div>
                  )}
                </div>

                {/* Video Description */}
                <div className="bg-gray-50 rounded-lg p-4">
                  <h3 className="font-semibold text-gray-900 mb-2">Description</h3>
                  <p className="text-gray-700 leading-relaxed whitespace-pre-wrap">
                    {video.description || 'No description available.'}
                  </p>
                </div>
              </CardContent>
            </Card>
          </div>

          {/* Sidebar */}
          <div className="space-y-6">
            {/* Video Stats */}
            <Card className="border-0 shadow-lg bg-white/80 backdrop-blur-sm">
              <CardContent className="p-6">
                <h3 className="font-semibold text-gray-900 mb-4 flex items-center gap-2">
                  <Eye className="w-5 h-5 text-blue-600" />
                  Video Stats
                </h3>
                <div className="space-y-3">
                  <div className="flex items-center justify-between p-3 bg-blue-50 rounded-lg">
                    <div className="flex items-center gap-2">
                      <Eye className="w-4 h-4 text-blue-600" />
                      <span className="text-sm font-medium text-blue-900">Views</span>
                    </div>
                    <span className="font-bold text-blue-600">{viewCount.toLocaleString()}</span>
                  </div>
                  <div className="flex items-center justify-between p-3 bg-red-50 rounded-lg">
                    <div className="flex items-center gap-2">
                      <Heart className="w-4 h-4 text-red-600" />
                      <span className="text-sm font-medium text-red-900">Likes</span>
                    </div>
                    <span className="font-bold text-red-600">{likeCount.toLocaleString()}</span>
                  </div>
                </div>
              </CardContent>
            </Card>

            {/* Creator Info */}
            <Card className="border-0 shadow-lg bg-white/80 backdrop-blur-sm">
              <CardContent className="p-6">
                <h3 className="font-semibold text-gray-900 mb-4 flex items-center gap-2">
                  <User className="w-5 h-5 text-purple-600" />
                  About Creator
                </h3>
                <div className="flex items-center gap-3 mb-4">
                  <Avatar className="h-16 w-16 ring-2 ring-purple-200 shadow-lg">
                    <AvatarImage src={video.user.avatar || '/placeholder.svg'} alt={video.user.name} />
                    <AvatarFallback className="bg-gradient-to-br from-purple-500 to-pink-600 text-white font-semibold text-lg">
                      {video.user.name?.charAt(0)?.toUpperCase() || 'U'}
                    </AvatarFallback>
                  </Avatar>
                  <div>
                    <h4 className="font-semibold text-gray-900">{video.user.name || 'Unknown Creator'}</h4>
                    <p className="text-sm text-gray-500">Content Creator</p>
                  </div>
                </div>
                {!isCreator && (
                  <Button className="w-full bg-gradient-to-r from-purple-600 to-pink-600 hover:from-purple-700 hover:to-pink-700 text-white">
                    Follow Creator
                  </Button>
                )}
              </CardContent>
            </Card>
          </div>
        </div>
      </div>
    </div>
  );
};

export default VideoWatch;
