import React, { useState, useEffect } from 'react';
import axios from 'axios';
import VideoCard from './VideoCard';
import { Video, TrendingUp, Users, Play, Upload, Zap, Clock, Star, ArrowRight } from 'lucide-react';
import { Link } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';

const Dashboard = () => {
  const { user } = useAuth();
  const [videos, setVideos] = useState([]);
  const [loading, setLoading] = useState(true);
  const [featuredVideos, setFeaturedVideos] = useState([]);
  const [trendingVideos, setTrendingVideos] = useState([]);
  const [shortVideos, setShortVideos] = useState([]);

  const containerStyle = {
    minHeight: '100vh',
    background: 'linear-gradient(135deg, #f8fafc 0%, #e2e8f0 100%)'
  };

  const heroStyle = {
    position: 'relative',
    overflow: 'hidden',
    background: 'linear-gradient(135deg, #667eea 0%, #764ba2 50%, #ec4899 100%)',
    color: 'white'
  };

  const heroOverlayStyle = {
    position: 'absolute',
    inset: 0,
    background: 'rgba(0, 0, 0, 0.2)'
  };

  const cardStyle = {
    background: 'rgba(255, 255, 255, 0.8)',
    backdropFilter: 'blur(10px)',
    borderRadius: '16px',
    boxShadow: '0 8px 32px rgba(0, 0, 0, 0.1)',
    border: '1px solid rgba(255, 255, 255, 0.2)',
    transition: 'all 0.3s ease'
  };

  const buttonStyle = {
    padding: '16px 32px',
    borderRadius: '8px',
    border: 'none',
    fontWeight: '600',
    fontSize: '1.125rem',
    cursor: 'pointer',
    transition: 'all 0.3s ease',
    display: 'inline-flex',
    alignItems: 'center',
    gap: '8px',
    textDecoration: 'none'
  };

  const primaryButtonStyle = {
    ...buttonStyle,
    background: 'white',
    color: '#1f2937',
    boxShadow: '0 8px 32px rgba(0, 0, 0, 0.2)'
  };

  const outlineButtonStyle = {
    ...buttonStyle,
    background: 'transparent',
    color: 'white',
    border: '2px solid white',
    boxShadow: '0 8px 32px rgba(0, 0, 0, 0.2)'
  };

  useEffect(() => {
    const fetchVideos = async () => {
      try {
        const response = await axios.get('/api/videos');
        const allVideos = response.data.videos || [];
        setVideos(allVideos);

        // Sort and categorize videos
        const sortedByViews = [...allVideos].sort((a, b) => (b.views?.length || 0) - (a.views?.length || 0));
        const sortedByRecent = [...allVideos].sort(
          (a, b) => new Date(b.createdAt).getTime() - new Date(a.createdAt).getTime()
        );

        setFeaturedVideos(sortedByViews.slice(0, 6));
        setTrendingVideos(sortedByRecent.slice(0, 8));
        setShortVideos(allVideos.filter((v) => v.videoType === 'short').slice(0, 6));
      } catch (error) {
        console.error('Error fetching videos:', error);
        setVideos([]);
      } finally {
        setLoading(false);
      }
    };

    fetchVideos();
  }, []);

  const stats = [
    { label: 'Total Videos', value: videos.length.toLocaleString(), icon: Video },
    { label: 'Active Creators', value: '1.2K', icon: Users },
    { label: 'Total Views', value: '50K+', icon: Play },
    { label: 'This Month', value: '+25%', icon: TrendingUp },
  ];

  const categories = [
    {
      title: 'Short Videos',
      description: 'Quick, engaging content under 45 seconds',
      icon: Zap,
      color: 'linear-gradient(135deg, #10b981 0%, #059669 100%)',
      count: videos.filter((v) => v.videoType === 'short').length,
    },
    {
      title: 'Long Form',
      description: 'In-depth content and storytelling',
      icon: Clock,
      color: 'linear-gradient(135deg, #8b5cf6 0%, #7c3aed 100%)',
      count: videos.filter((v) => v.videoType === 'long').length,
    },
    {
      title: 'Featured',
      description: 'Handpicked by our community',
      icon: Star,
      color: 'linear-gradient(135deg, #f59e0b 0%, #d97706 100%)',
      count: featuredVideos.length,
    },
  ];

  const LoadingSkeleton = () => (
    <div style={{ padding: '48px 16px' }}>
      <div style={{ maxWidth: '1400px', margin: '0 auto' }}>
        <div style={{ display: 'flex', flexDirection: 'column', gap: '48px' }}>
          {/* Hero Skeleton */}
          <div style={{ textAlign: 'center', display: 'flex', flexDirection: 'column', gap: '16px' }}>
            <div style={{ height: '48px', width: '384px', background: '#f3f4f6', borderRadius: '8px', margin: '0 auto', animation: 'pulse 2s infinite' }} />
            <div style={{ height: '24px', width: '600px', background: '#f3f4f6', borderRadius: '8px', margin: '0 auto', animation: 'pulse 2s infinite' }} />
            <div style={{ display: 'flex', justifyContent: 'center', gap: '16px' }}>
              <div style={{ height: '48px', width: '128px', background: '#f3f4f6', borderRadius: '8px', animation: 'pulse 2s infinite' }} />
              <div style={{ height: '48px', width: '128px', background: '#f3f4f6', borderRadius: '8px', animation: 'pulse 2s infinite' }} />
            </div>
          </div>

          {/* Stats Skeleton */}
          <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))', gap: '16px' }}>
            {Array.from({ length: 4 }).map((_, i) => (
              <div key={i} style={cardStyle}>
                <div style={{ padding: '24px', textAlign: 'center' }}>
                  <div style={{ width: '48px', height: '48px', borderRadius: '50%', background: '#f3f4f6', margin: '0 auto 12px', animation: 'pulse 2s infinite' }} />
                  <div style={{ height: '32px', width: '64px', background: '#f3f4f6', borderRadius: '4px', margin: '0 auto 8px', animation: 'pulse 2s infinite' }} />
                  <div style={{ height: '16px', width: '80px', background: '#f3f4f6', borderRadius: '4px', margin: '0 auto', animation: 'pulse 2s infinite' }} />
                </div>
              </div>
            ))}
          </div>

          {/* Videos Skeleton */}
          <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fill, minmax(300px, 1fr))', gap: '24px' }}>
            {Array.from({ length: 8 }).map((_, i) => (
              <div key={i} style={cardStyle}>
                <div style={{ aspectRatio: '16/9', background: '#f3f4f6', animation: 'pulse 2s infinite' }} />
                <div style={{ padding: '16px' }}>
                  <div style={{ height: '16px', width: '75%', background: '#f3f4f6', borderRadius: '4px', marginBottom: '8px', animation: 'pulse 2s infinite' }} />
                  <div style={{ height: '12px', width: '100%', background: '#f3f4f6', borderRadius: '4px', marginBottom: '8px', animation: 'pulse 2s infinite' }} />
                  <div style={{ height: '12px', width: '60%', background: '#f3f4f6', borderRadius: '4px', animation: 'pulse 2s infinite' }} />
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );

  if (loading) {
    return (
      <div style={containerStyle}>
        <LoadingSkeleton />
      </div>
    );
  }

  return (
    <div style={containerStyle}>
      {/* Hero Section */}
      <div style={heroStyle}>
        <div style={heroOverlayStyle} />
        <div style={{ position: 'relative', maxWidth: '1400px', margin: '0 auto', padding: '80px 16px', textAlign: 'center' }}>
          <div style={{ maxWidth: '800px', margin: '0 auto' }}>
            <h1 style={{ fontSize: 'clamp(2.5rem, 5vw, 4.5rem)', fontWeight: 'bold', marginBottom: '24px', lineHeight: '1.1' }}>
              Welcome to{' '}
              <span style={{ background: 'linear-gradient(135deg, #fbbf24 0%, #f59e0b 100%)', WebkitBackgroundClip: 'text', WebkitTextFillColor: 'transparent' }}>
                VlogApp
              </span>
            </h1>
            <p style={{ fontSize: 'clamp(1.125rem, 2vw, 1.5rem)', marginBottom: '32px', color: 'rgba(255, 255, 255, 0.9)', lineHeight: '1.6' }}>
              Discover amazing videos from creators around the world. Share your story, connect with your audience, and
              build your community.
            </p>
            <div style={{ display: 'flex', flexDirection: 'column', gap: '16px', alignItems: 'center' }}>
              <div style={{ display: 'flex', flexWrap: 'wrap', gap: '16px', justifyContent: 'center' }}>
                {user ? (
                  <>
                    <Link to="/upload" style={primaryButtonStyle}>
                      <Upload size={20} />
                      Upload Your Video
                    </Link>
                    <Link to="/videos" style={outlineButtonStyle}>
                      <Play size={20} />
                      Explore Videos
                    </Link>
                  </>
                ) : (
                  <>
                    <Link to="/register" style={primaryButtonStyle}>
                      <Video size={20} />
                      Get Started Free
                    </Link>
                    <Link to="/videos" style={outlineButtonStyle}>
                      <Play size={20} />
                      Watch Videos
                    </Link>
                  </>
                )}
              </div>
            </div>
          </div>
        </div>
      </div>

      <div style={{ maxWidth: '1400px', margin: '0 auto', padding: '48px 16px' }}>
        <div style={{ display: 'flex', flexDirection: 'column', gap: '64px' }}>
          {/* Stats Section */}
          <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))', gap: '16px' }}>
            {stats.map((stat, index) => {
              const IconComponent = stat.icon;
              return (
                <div
                  key={index}
                  style={{
                    ...cardStyle,
                    cursor: 'pointer',
                    transform: 'translateY(0)',
                    ':hover': { transform: 'translateY(-4px)', boxShadow: '0 12px 40px rgba(0, 0, 0, 0.15)' }
                  }}
                  onMouseEnter={(e) => {
                    e.currentTarget.style.transform = 'translateY(-4px)';
                    e.currentTarget.style.boxShadow = '0 12px 40px rgba(0, 0, 0, 0.15)';
                  }}
                  onMouseLeave={(e) => {
                    e.currentTarget.style.transform = 'translateY(0)';
                    e.currentTarget.style.boxShadow = '0 8px 32px rgba(0, 0, 0, 0.1)';
                  }}
                >
                  <div style={{ padding: '24px', textAlign: 'center' }}>
                    <div style={{
                      width: '48px',
                      height: '48px',
                      background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
                      borderRadius: '50%',
                      display: 'flex',
                      alignItems: 'center',
                      justifyContent: 'center',
                      margin: '0 auto 12px'
                    }}>
                      <IconComponent size={24} color="white" />
                    </div>
                    <div style={{ fontSize: '1.5rem', fontWeight: 'bold', color: '#1f2937', marginBottom: '4px' }}>{stat.value}</div>
                    <div style={{ fontSize: '0.875rem', color: '#6b7280' }}>{stat.label}</div>
                  </div>
                </div>
              );
            })}
          </div>

          {/* Categories Section */}
          <div style={{ display: 'flex', flexDirection: 'column', gap: '32px' }}>
            <div style={{ textAlign: 'center' }}>
              <h2 style={{ fontSize: '2rem', fontWeight: 'bold', color: '#1f2937', marginBottom: '16px' }}>Explore by Category</h2>
              <p style={{ color: '#6b7280', fontSize: '1.125rem' }}>Find the perfect content for your mood</p>
            </div>
            <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))', gap: '24px' }}>
              {categories.map((category, index) => {
                const IconComponent = category.icon;
                return (
                  <div
                    key={index}
                    style={{
                      ...cardStyle,
                      cursor: 'pointer',
                      transform: 'translateY(0)'
                    }}
                    onMouseEnter={(e) => {
                      e.currentTarget.style.transform = 'translateY(-4px)';
                      e.currentTarget.style.boxShadow = '0 12px 40px rgba(0, 0, 0, 0.15)';
                    }}
                    onMouseLeave={(e) => {
                      e.currentTarget.style.transform = 'translateY(0)';
                      e.currentTarget.style.boxShadow = '0 8px 32px rgba(0, 0, 0, 0.1)';
                    }}
                  >
                    <div style={{ padding: '24px' }}>
                      <div style={{
                        width: '48px',
                        height: '48px',
                        background: category.color,
                        borderRadius: '8px',
                        display: 'flex',
                        alignItems: 'center',
                        justifyContent: 'center',
                        marginBottom: '16px',
                        transition: 'transform 0.3s ease'
                      }}>
                        <IconComponent size={24} color="white" />
                      </div>
                      <h3 style={{ fontSize: '1.25rem', fontWeight: '600', color: '#1f2937', marginBottom: '8px' }}>{category.title}</h3>
                      <p style={{ color: '#6b7280', marginBottom: '16px' }}>{category.description}</p>
                      <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
                        <div style={{
                          background: '#f3f4f6',
                          color: '#374151',
                          padding: '4px 8px',
                          borderRadius: '4px',
                          fontSize: '0.875rem'
                        }}>
                          {category.count} videos
                        </div>
                        <ArrowRight size={16} color="#9ca3af" style={{ transition: 'all 0.3s ease' }} />
                      </div>
                    </div>
                  </div>
                );
              })}
            </div>
          </div>

          {videos.length === 0 ? (
            /* Empty State */
            <div style={cardStyle}>
              <div style={{ padding: '48px', textAlign: 'center' }}>
                <div style={{
                  width: '80px',
                  height: '80px',
                  margin: '0 auto 24px',
                  background: '#dbeafe',
                  borderRadius: '50%',
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center'
                }}>
                  <Video size={40} color="#3b82f6" />
                </div>
                <h3 style={{ fontSize: '1.5rem', fontWeight: 'bold', color: '#1f2937', marginBottom: '8px' }}>No videos yet</h3>
                <p style={{ color: '#6b7280', marginBottom: '24px', maxWidth: '400px', margin: '0 auto 24px' }}>
                  Be the first to share your story! Upload a video and start building our amazing community.
                </p>
                <div style={{ display: 'flex', flexDirection: 'column', gap: '12px', alignItems: 'center' }}>
                  <div style={{ display: 'flex', flexWrap: 'wrap', gap: '12px', justifyContent: 'center' }}>
                    <Link
                      to={user ? '/upload' : '/register'}
                      style={{
                        ...buttonStyle,
                        background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
                        color: 'white'
                      }}
                    >
                      <Upload size={16} />
                      {user ? 'Upload First Video' : 'Join & Upload'}
                    </Link>
                  </div>
                </div>
              </div>
            </div>
          ) : (
            <>
              {/* Featured Videos */}
              {featuredVideos.length > 0 && (
                <div style={{ display: 'flex', flexDirection: 'column', gap: '32px' }}>
                  <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', flexWrap: 'wrap', gap: '16px' }}>
                    <div>
                      <h2 style={{ fontSize: '2rem', fontWeight: 'bold', color: '#1f2937', marginBottom: '8px' }}>Featured Videos</h2>
                      <p style={{ color: '#6b7280' }}>Most popular content from our community</p>
                    </div>
                    <Link
                      to="/videos"
                      style={{
                        ...buttonStyle,
                        background: 'rgba(255, 255, 255, 0.8)',
                        color: '#374151',
                        border: '1px solid #e5e7eb',
                        padding: '8px 16px',
                        fontSize: '0.875rem'
                      }}
                    >
                      View All
                      <ArrowRight size={16} />
                    </Link>
                  </div>
                  <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fill, minmax(300px, 1fr))', gap: '24px' }}>
                    {featuredVideos.slice(0, 6).map((video) => (
                      <VideoCard key={video._id} video={video} />
                    ))}
                  </div>
                </div>
              )}

              {/* Short Videos */}
              {shortVideos.length > 0 && (
                <div style={{ display: 'flex', flexDirection: 'column', gap: '32px' }}>
                  <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', flexWrap: 'wrap', gap: '16px' }}>
                    <div>
                      <h2 style={{ fontSize: '2rem', fontWeight: 'bold', color: '#1f2937', marginBottom: '8px', display: 'flex', alignItems: 'center', gap: '8px' }}>
                        <Zap size={32} color="#10b981" />
                        Short Videos
                      </h2>
                      <p style={{ color: '#6b7280' }}>Quick, engaging content under 45 seconds</p>
                    </div>
                    <Link
                      to="/videos?filter=short"
                      style={{
                        ...buttonStyle,
                        background: 'rgba(255, 255, 255, 0.8)',
                        color: '#374151',
                        border: '1px solid #e5e7eb',
                        padding: '8px 16px',
                        fontSize: '0.875rem'
                      }}
                    >
                      View All
                      <ArrowRight size={16} />
                    </Link>
                  </div>
                  <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fill, minmax(280px, 1fr))', gap: '24px' }}>
                    {shortVideos.map((video) => (
                      <VideoCard key={video._id} video={video} />
                    ))}
                  </div>
                </div>
              )}

              {/* Recent Videos */}
              <div style={{ display: 'flex', flexDirection: 'column', gap: '32px' }}>
                <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', flexWrap: 'wrap', gap: '16px' }}>
                  <div>
                    <h2 style={{ fontSize: '2rem', fontWeight: 'bold', color: '#1f2937', marginBottom: '8px' }}>Latest Uploads</h2>
                    <p style={{ color: '#6b7280' }}>Fresh content from our creators</p>
                  </div>
                  <Link
                    to="/videos"
                    style={{
                      ...buttonStyle,
                      background: 'rgba(255, 255, 255, 0.8)',
                      color: '#374151',
                      border: '1px solid #e5e7eb',
                      padding: '8px 16px',
                      fontSize: '0.875rem'
                    }}
                  >
                    View All
                    <ArrowRight size={16} />
                  </Link>
                </div>
                <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fill, minmax(300px, 1fr))', gap: '24px' }}>
                  {trendingVideos.map((video) => (
                    <VideoCard key={video._id} video={video} />
                  ))}
                </div>
              </div>
            </>
          )}

          {/* Call to Action */}
          <div style={{
            ...cardStyle,
            background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
            color: 'white',
            overflow: 'hidden',
            position: 'relative'
          }}>
            <div style={{ position: 'absolute', inset: 0, background: 'rgba(0, 0, 0, 0.1)' }} />
            <div style={{ position: 'relative', padding: '48px', textAlign: 'center' }}>
              <h2 style={{ fontSize: '2rem', fontWeight: 'bold', marginBottom: '16px' }}>Ready to Share Your Story?</h2>
              <p style={{ fontSize: '1.25rem', marginBottom: '32px', color: 'rgba(255, 255, 255, 0.9)', maxWidth: '600px', margin: '0 auto 32px' }}>
                Join thousands of creators who are already sharing their passion and building their audience on VlogApp.
              </p>
              <div style={{ display: 'flex', flexDirection: 'column', gap: '16px', alignItems: 'center' }}>
                <div style={{ display: 'flex', flexWrap: 'wrap', gap: '16px', justifyContent: 'center' }}>
                  <Link
                    to={user ? '/upload' : '/register'}
                    style={{
                      ...buttonStyle,
                      background: 'white',
                      color: '#1f2937',
                      boxShadow: '0 8px 32px rgba(0, 0, 0, 0.2)'
                    }}
                  >
                    <Video size={20} />
                    {user ? 'Upload Now' : 'Start Creating'}
                  </Link>
                  {!user && (
                    <Link
                      to="/login"
                      style={{
                        ...buttonStyle,
                        background: 'transparent',
                        color: 'white',
                        border: '2px solid white',
                        boxShadow: '0 8px 32px rgba(0, 0, 0, 0.2)'
                      }}
                    >
                      <Play size={20} />
                      Sign In
                    </Link>
                  )}
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
