import React, { useState, useEffect } from 'react';
import axios from 'axios';
import VideoCard from './VideoCard';
import { Card, CardContent } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Skeleton } from '@/components/ui/skeleton';
import { Video, TrendingUp, Users, Play, Upload, Zap, Clock, Star, ArrowRight } from 'lucide-react';
import { Link } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';

const Dashboard = () => {
  const { user } = useAuth();
  const [videos, setVideos] = useState([]);
  const [loading, setLoading] = useState(true);
  const [featuredVideos, setFeaturedVideos] = useState([]);
  const [trendingVideos, setTrendingVideos] = useState([]);
  const [shortVideos, setShortVideos] = useState([]);

  useEffect(() => {
    const fetchVideos = async () => {
      try {
        const response = await axios.get('/api/videos');
        const allVideos = response.data.videos || [];
        setVideos(allVideos);

        // Sort and categorize videos
        const sortedByViews = [...allVideos].sort((a, b) => (b.views?.length || 0) - (a.views?.length || 0));
        const sortedByRecent = [...allVideos].sort(
          (a, b) => new Date(b.createdAt).getTime() - new Date(a.createdAt).getTime()
        );

        setFeaturedVideos(sortedByViews.slice(0, 6));
        setTrendingVideos(sortedByRecent.slice(0, 8));
        setShortVideos(allVideos.filter((v) => v.videoType === 'short').slice(0, 6));
      } catch (error) {
        console.error('Error fetching videos:', error);
        setVideos([]);
      } finally {
        setLoading(false);
      }
    };

    fetchVideos();
  }, []);

  const stats = [
    { label: 'Total Videos', value: videos.length.toLocaleString(), icon: Video },
    { label: 'Active Creators', value: '1.2K', icon: Users },
    { label: 'Total Views', value: '50K+', icon: Play },
    { label: 'This Month', value: '+25%', icon: TrendingUp },
  ];

  const categories = [
    {
      title: 'Short Videos',
      description: 'Quick, engaging content under 45 seconds',
      icon: Zap,
      color: 'from-green-500 to-emerald-600',
      count: videos.filter((v) => v.videoType === 'short').length,
    },
    {
      title: 'Long Form',
      description: 'In-depth content and storytelling',
      icon: Clock,
      color: 'from-purple-500 to-violet-600',
      count: videos.filter((v) => v.videoType === 'long').length,
    },
    {
      title: 'Featured',
      description: 'Handpicked by our community',
      icon: Star,
      color: 'from-yellow-500 to-orange-600',
      count: featuredVideos.length,
    },
  ];

  const LoadingSkeleton = () => (
    <div className="space-y-12">
      {/* Hero Skeleton */}
      <div className="text-center space-y-4">
        <Skeleton className="h-12 w-96 mx-auto" />
        <Skeleton className="h-6 w-[600px] mx-auto" />
        <div className="flex justify-center gap-4">
          <Skeleton className="h-12 w-32" />
          <Skeleton className="h-12 w-32" />
        </div>
      </div>

      {/* Stats Skeleton */}
      <div className="grid grid-cols-2 lg:grid-cols-4 gap-4">
        {Array.from({ length: 4 }).map((_, i) => (
          <Card key={i} className="border-0 shadow-lg">
            <CardContent className="p-6 text-center">
              <Skeleton className="h-12 w-12 rounded-full mx-auto mb-3" />
              <Skeleton className="h-8 w-16 mx-auto mb-2" />
              <Skeleton className="h-4 w-20 mx-auto" />
            </CardContent>
          </Card>
        ))}
      </div>

      {/* Videos Skeleton */}
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
        {Array.from({ length: 8 }).map((_, i) => (
          <Card key={i} className="overflow-hidden border-0 shadow-lg">
            <Skeleton className="aspect-video w-full" />
            <CardContent className="p-4">
              <Skeleton className="h-4 w-3/4 mb-2" />
              <Skeleton className="h-3 w-full mb-2" />
              <Skeleton className="h-3 w-2/3" />
            </CardContent>
          </Card>
        ))}
      </div>
    </div>
  );

  if (loading) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-slate-50 to-slate-100 p-4">
        <div className="max-w-7xl mx-auto py-12">
          <LoadingSkeleton />
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 to-slate-100">
      {/* Hero Section */}
      <div className="relative overflow-hidden bg-gradient-to-r from-blue-600 via-purple-600 to-pink-600 text-white">
        <div className="absolute inset-0 bg-black/20" />
        <div className="relative max-w-7xl mx-auto px-4 py-20 text-center">
          <div className="max-w-4xl mx-auto">
            <h1 className="text-5xl md:text-7xl font-bold mb-6 leading-tight">
              Welcome to{' '}
              <span className="bg-gradient-to-r from-yellow-300 to-orange-300 bg-clip-text text-transparent">
                VlogApp
              </span>
            </h1>
            <p className="text-xl md:text-2xl mb-8 text-white/90 leading-relaxed">
              Discover amazing videos from creators around the world. Share your story, connect with your audience, and
              build your community.
            </p>
            <div className="flex flex-col sm:flex-row gap-4 justify-center">
              {user ? (
                <>
                  <Button
                    asChild
                    size="lg"
                    className="bg-white text-gray-900 hover:bg-gray-100 shadow-xl hover:shadow-2xl transition-all duration-200 text-lg px-8 py-4"
                  >
                    <Link to="/upload" className="flex items-center gap-2">
                      <Upload className="w-5 h-5" />
                      Upload Your Video
                    </Link>
                  </Button>
                  <Button
                    asChild
                    size="lg"
                    variant="outline"
                    className="border-white text-white hover:bg-white hover:text-gray-900 shadow-xl hover:shadow-2xl transition-all duration-200 text-lg px-8 py-4 bg-transparent"
                  >
                    <Link to="/videos" className="flex items-center gap-2">
                      <Play className="w-5 h-5" />
                      Explore Videos
                    </Link>
                  </Button>
                </>
              ) : (
                <>
                  <Button
                    asChild
                    size="lg"
                    className="bg-white text-gray-900 hover:bg-gray-100 shadow-xl hover:shadow-2xl transition-all duration-200 text-lg px-8 py-4"
                  >
                    <Link to="/register" className="flex items-center gap-2">
                      <Video className="w-5 h-5" />
                      Get Started Free
                    </Link>
                  </Button>
                  <Button
                    asChild
                    size="lg"
                    variant="outline"
                    className="border-white text-white hover:bg-white hover:text-gray-900 shadow-xl hover:shadow-2xl transition-all duration-200 text-lg px-8 py-4 bg-transparent"
                  >
                    <Link to="/videos" className="flex items-center gap-2">
                      <Play className="w-5 h-5" />
                      Watch Videos
                    </Link>
                  </Button>
                </>
              )}
            </div>
          </div>
        </div>
      </div>

      <div className="max-w-7xl mx-auto px-4 py-12 space-y-16">
        {/* Stats Section */}
        <div className="grid grid-cols-2 lg:grid-cols-4 gap-4">
          {stats.map((stat, index) => {
            const IconComponent = stat.icon;
            return (
              <Card
                key={index}
                className="border-0 shadow-lg bg-white/80 backdrop-blur-sm hover:shadow-xl transition-all duration-200"
              >
                <CardContent className="p-6 text-center">
                  <div className="w-12 h-12 bg-gradient-to-r from-blue-500 to-purple-600 rounded-full flex items-center justify-center mx-auto mb-3">
                    <IconComponent className="w-6 h-6 text-white" />
                  </div>
                  <div className="text-2xl font-bold text-gray-900 mb-1">{stat.value}</div>
                  <div className="text-sm text-gray-600">{stat.label}</div>
                </CardContent>
              </Card>
            );
          })}
        </div>

        {/* Categories Section */}
        <div className="space-y-8">
          <div className="text-center">
            <h2 className="text-3xl font-bold text-gray-900 mb-4">Explore by Category</h2>
            <p className="text-gray-600 text-lg">Find the perfect content for your mood</p>
          </div>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
            {categories.map((category, index) => {
              const IconComponent = category.icon;
              return (
                <Card
                  key={index}
                  className="border-0 shadow-lg bg-white/80 backdrop-blur-sm hover:shadow-xl transition-all duration-200 group cursor-pointer"
                >
                  <CardContent className="p-6">
                    <div
                      className={`w-12 h-12 bg-gradient-to-r ${category.color} rounded-lg flex items-center justify-center mb-4 group-hover:scale-110 transition-transform duration-200`}
                    >
                      <IconComponent className="w-6 h-6 text-white" />
                    </div>
                    <h3 className="text-xl font-semibold text-gray-900 mb-2">{category.title}</h3>
                    <p className="text-gray-600 mb-4">{category.description}</p>
                    <div className="flex items-center justify-between">
                      <Badge variant="secondary" className="bg-gray-100">
                        {category.count} videos
                      </Badge>
                      <ArrowRight className="w-4 h-4 text-gray-400 group-hover:text-gray-600 group-hover:translate-x-1 transition-all duration-200" />
                    </div>
                  </CardContent>
                </Card>
              );
            })}
          </div>
        </div>

        {videos.length === 0 ? (
          /* Empty State */
          <Card className="border-0 shadow-xl bg-white/80 backdrop-blur-sm">
            <CardContent className="p-12 text-center">
              <div className="w-20 h-20 mx-auto mb-6 bg-blue-100 rounded-full flex items-center justify-center">
                <Video className="w-10 h-10 text-blue-600" />
              </div>
              <h3 className="text-2xl font-bold text-gray-900 mb-2">No videos yet</h3>
              <p className="text-gray-600 mb-6 max-w-md mx-auto">
                Be the first to share your story! Upload a video and start building our amazing community.
              </p>
              <div className="flex flex-col sm:flex-row gap-3 justify-center">
                <Button
                  asChild
                  className="bg-gradient-to-r from-blue-600 to-purple-600 hover:from-blue-700 hover:to-purple-700 text-white"
                >
                  <Link to={user ? '/upload' : '/register'} className="flex items-center gap-2">
                    <Upload className="w-4 h-4" />
                    {user ? 'Upload First Video' : 'Join & Upload'}
                  </Link>
                </Button>
              </div>
            </CardContent>
          </Card>
        ) : (
          <>
            {/* Featured Videos */}
            {featuredVideos.length > 0 && (
              <div className="space-y-8">
                <div className="flex items-center justify-between">
                  <div>
                    <h2 className="text-3xl font-bold text-gray-900 mb-2">Featured Videos</h2>
                    <p className="text-gray-600">Most popular content from our community</p>
                  </div>
                  <Button asChild variant="outline" className="bg-white/80 backdrop-blur-sm">
                    <Link to="/videos" className="flex items-center gap-2">
                      View All
                      <ArrowRight className="w-4 h-4" />
                    </Link>
                  </Button>
                </div>
                <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
                  {featuredVideos.slice(0, 6).map((video) => (
                    <VideoCard key={video._id} video={video} />
                  ))}
                </div>
              </div>
            )}

            {/* Short Videos */}
            {shortVideos.length > 0 && (
              <div className="space-y-8">
                <div className="flex items-center justify-between">
                  <div>
                    <h2 className="text-3xl font-bold text-gray-900 mb-2 flex items-center gap-2">
                      <Zap className="w-8 h-8 text-green-600" />
                      Short Videos
                    </h2>
                    <p className="text-gray-600">Quick, engaging content under 45 seconds</p>
                  </div>
                  <Button asChild variant="outline" className="bg-white/80 backdrop-blur-sm">
                    <Link to="/videos?filter=short" className="flex items-center gap-2">
                      View All
                      <ArrowRight className="w-4 h-4" />
                    </Link>
                  </Button>
                </div>
                <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
                  {shortVideos.map((video) => (
                    <VideoCard key={video._id} video={video} />
                  ))}
                </div>
              </div>
            )}

            {/* Recent Videos */}
            <div className="space-y-8">
              <div className="flex items-center justify-between">
                <div>
                  <h2 className="text-3xl font-bold text-gray-900 mb-2">Latest Uploads</h2>
                  <p className="text-gray-600">Fresh content from our creators</p>
                </div>
                <Button asChild variant="outline" className="bg-white/80 backdrop-blur-sm">
                  <Link to="/videos" className="flex items-center gap-2">
                    View All
                    <ArrowRight className="w-4 h-4" />
                  </Link>
                </Button>
              </div>
              <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
                {trendingVideos.map((video) => (
                  <VideoCard key={video._id} video={video} />
                ))}
              </div>
            </div>
          </>
        )}

        {/* Call to Action */}
        <Card className="border-0 shadow-xl bg-gradient-to-r from-blue-600 to-purple-600 text-white overflow-hidden">
          <div className="absolute inset-0 bg-black/10" />
          <CardContent className="relative p-12 text-center">
            <h2 className="text-3xl font-bold mb-4">Ready to Share Your Story?</h2>
            <p className="text-xl mb-8 text-white/90 max-w-2xl mx-auto">
              Join thousands of creators who are already sharing their passion and building their audience on VlogApp.
            </p>
            <div className="flex flex-col sm:flex-row gap-4 justify-center">
              <Button
                asChild
                size="lg"
                className="bg-white text-gray-900 hover:bg-gray-100 shadow-xl hover:shadow-2xl transition-all duration-200"
              >
                <Link to={user ? '/upload' : '/register'} className="flex items-center gap-2">
                  <Video className="w-5 h-5" />
                  {user ? 'Upload Now' : 'Start Creating'}
                </Link>
              </Button>
              {!user && (
                <Button
                  asChild
                  size="lg"
                  variant="outline"
                  className="border-white text-white hover:bg-white hover:text-gray-900 shadow-xl hover:shadow-2xl transition-all duration-200 bg-transparent"
                >
                  <Link to="/login" className="flex items-center gap-2">
                    <Play className="w-5 h-5" />
                    Sign In
                  </Link>
                </Button>
              )}
            </div>
          </CardContent>
        </Card>
      </div>
    </div>
  );
};

export default Dashboard;
